import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import SearchIcon from '@mui/icons-material/Search';
import AccountCircle from '@mui/icons-material/AccountCircle';
import { Link } from 'react-router-dom';
import { reactLocalStorage } from 'reactjs-localstorage';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useFormik } from 'formik';
import Input from '@mui/material/Input';
import FormControl from '@mui/material/FormControl';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import { ReactComponent as Logo } from './logo.svg';

export default function Navbar() {
  const [Check, setCheck] = React.useState(false);
  const navigate = useNavigate();

  function logout() {
    const userID = reactLocalStorage.get('userID');
    axios
      .post('https://pacific-sands-58031.herokuapp.com/user/logout', {
        userID: userID,
      })
      .then(function (response) {
        if (response.data.message === 'success') {
          reactLocalStorage.remove('token');
          reactLocalStorage.remove('userID');
          navigate('/');
          window.location.reload(true);
        } else {
          alert(response.data.message);
        }
      });
  }

  function viewprofile() {
    navigate('/CustomerProfile');
  }

  function history() {
    navigate('/History');
  }

  function products() {
    navigate('/myproducts');
  }

  function wallet() {
    navigate('/wallet');
  }

  function chat() {
    navigate('/chatlist');
  }

  React.useEffect(() => {
    const username = reactLocalStorage.get('userID', '', true);
    if (username !== '') {
      setCheck(true);
    }
  }, []);

  const formik = useFormik({
    initialValues: {
      name: '',
    },
    onSubmit: (values) => {
      axios
        .post('https://pacific-sands-58031.herokuapp.com/product/search', {
          name: values.name,
        })
        .then(function (response) {
          if (response.data.message === 'success') {
            navigate('/Search', { state: { data: response.data, admin: false } });
          } else {
            alert('Object Not Found');
          }
        })
        .catch(function (error) {
          console.log(error);
          alert('Error');
        });
    },
  });

  const [anchorEl1, setAnchorEl1] = React.useState(null);
  const [anchorEl2, setAnchorEl2] = React.useState(null);
  const [anchorEl3, setAnchorEl3] = React.useState(null);

  const handleClick1 = (event) => {
    setAnchorEl1(event.currentTarget);
  };

  const handleClick2 = (event) => {
    setAnchorEl2(event.currentTarget);
  };

  const handleClick3 = (event) => {
    setAnchorEl3(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl1(null);
    setAnchorEl2(null);
    setAnchorEl3(null);
  };

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="fixed" sx={{ backgroundColor: 'black' }}>
        <Toolbar>
          <Link reloadDocument to={{ pathname: '/' }} style={{ margin: '0 15px', color: '#a7ac38' }}>
            <Logo style={{ width: '200px', height: '70px' }} className="d-inline-block" />
          </Link>
          <button className="btn" id="demo-positioned-button2" aria-controls="menu2" aria-haspopup="true" onClick={handleClick2} style={{ fontSize: '150%', marginLeft: '20px', color: 'white' }}>
            Categories
          </button>
          {/* Other menu items go here */}

          {/* Sell with us button */}
          <button
            className="btn"
            data-cy="sell-button"
            onClick={() => {
              navigate('/AddProduct');
            }}
            style={{ fontSize: '150%', marginLeft: '20px', borderRadius: '10px', backgroundColor: '#58811c', color: 'white' }}
          >
            Sell With Us
          </button>

          {/* Search input */}
          <div className="col text-right">
            <FormControl sx={{ backgroundColor: 'white', borderRadius: '15px' }} variant="standard">
              <Input
                type={'text'}
                id="name"
                disableUnderline="false"
                placeholder="Search Product"
                style={{ paddingLeft: '10px', fontSize: '15px' }}
                inputProps={{ style: { paddingLeft: '20px' } }}
                onChange={formik.handleChange}
                endAdornment={
                  <IconButton onClick={formik.handleSubmit}>
                    <SearchIcon />
                  </IconButton>
                }
                label="search"
              />
            </FormControl>
          </div>

          {/* Profile and Sign In buttons */}
          {Check ? (
            <IconButton
              size="large"
              id="profile-icon"
              aria-label="account of current user"
              aria-controls="menu3"
              aria-haspopup="true"
              onClick={handleClick3}
              color="inherit"
            >
              <AccountCircle sx={{ fontSize: '200%' }} />
            </IconButton>
          ) : (
            <button
              className="btn"
              onClick={() => {
                navigate('/login');
              }}
              style={{ fontSize: '150%', color: 'white', borderRadius: '10px', margin: '0 0px 0 20px', backgroundColor: '#58811c' }}
            >
              Sign In
            </button>
          )}

          {/* Profile menu */}
          <Menu id="menu3" anchorEl={anchorEl3} keepMounted open={Boolean(anchorEl3)} onClose={handleClose}>
            <MenuItem id="profile-id" onClick={viewprofile}>
              Profile
            </MenuItem>
            <MenuItem id="wallet-id" onClick={wallet}>
              Wallet
            </MenuItem>
            <MenuItem onClick={products}>My Products</MenuItem>
            <MenuItem onClick={history}>Bidding History</MenuItem>
            <MenuItem onClick={chat}>Chat</MenuItem>
            <MenuItem onClick={logout}>Logout</MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>
      {/* Add spacing at the bottom */}
      <Box sx={{ height: '64px' }} />
    </Box>
  );
}
